import React, { useEffect, useState } from "react";

import axios from "axios";
import { Button, Card } from "react-bootstrap";

const Homepage = () => {
  const [data, getData] = useState([]);
  const [filter, getFilter] = useState([]);
  const [filterprice, setfilterprice] = useState({
    starting: "",
    ending: "",
  });
  const gettingdata = () => {
    axios.get("https://fakestoreapi.com/products").then((response) => {
      getData(response.data);
      getFilter(response.data);
      console.log(response.data);
    });
  };

  const filteritem = (event) => {
    console.log(event, "yuygfuyfuyf");
    getData(
      filter.filter((datas) => {
        // console.log(datas.dis_feetype_name);
        return datas.category
          .toString()
          .toUpperCase()
          .includes(event.target.value.toUpperCase());
      })
    );
  };
  const filtereditem = (e) => {
    console.log(e, "yuygfuyfuyf");
    getData(
      filter.filter((datas) => {
        let startingprice = e.target.value;
        let price = datas.price;
        // console.log(datas.dis_feetype_name);
        return price > e.target.value && price < e.target.value;
      })
    );
  };
  const handleChange = (e) => {
    setfilterprice({ ...filterprice, [e.target.name]: e.target.value });
  };
  useEffect(() => {
    gettingdata();
  }, []);
  return (
    <div>
      <div className="container">
        <Card>
          Filter
          <input
            style={{ width: "25%" }}
            type="text"
            name="starting"
            class="form-control"
            placeholder="Enter starting price"
            onChange={handleChange}
            value={filterprice.starting}
          ></input>
          <input
            style={{ width: "25%" }}
            type="text"
            class="form-control"
            name="ending"
            placeholder="Enter ending price"
            onChange={handleChange}
            value={filterprice.ending}
          ></input>
          <Button onClick={filtereditem}>submit</Button>
          <input
            style={{ width: "25%" }}
            type="search"
            class="form-control"
            placeholder="type to search"
            onChange={(event) => {
              filteritem(event);
            }}
          ></input>
        </Card>
        <div className="row">
          {data.map((value) => {
            return (
              <div className="col -4">
                <div
                  className="card-body"
                  style={{
                    width: "18rem",
                    margin: "4%",
                    backgroundColor: "lightgrey",
                    borderBlockColor: "cyan",
                  }}
                >
                  <img
                    className="card-img-top"
                    src={value.image}
                    alt="Card image cap"
                  />
                  <h6 className="card-title">{`Id:${value.id}`}</h6>
                  <h6 className="card-title">{`title:${value.title}`}</h6>
                  <h6 className="card-title">{`Price:${value.price}`}</h6>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
