import React, { useEffect, useState } from "react";

import axios from "axios";
import { Card, Form, Dropdown, DropdownButton } from "react-bootstrap";

const Homepage = () => {
  const [data, getData] = useState([]);
  const [filter, getFilter] = useState([]);
  const [price, setPrice] = useState({
    start: "",
    end: "",
  });
  const [formError, setFormError] = useState({});
  const [selectedItem, setSelectedItem] = useState("");

  const onChangeprice = (e) => {
    setPrice({ ...price, [e.target.name]: e.target.value });
  };

  const gettingdata = () => {
    axios.get("https://fakestoreapi.com/products").then((response) => {
      getData(response.data);
      getFilter(response.data);
    });
  };

  const filteritem = (event) => {
    getData(
      filter.filter((datas) => {
        // console.log(datas.dis_feetype_name);
        return datas.title
          .toString()
          .toUpperCase()
          .includes(event.target.value.toUpperCase());
      })
    );
  };
  const filtereditem = (e) => {
    getData(
      filter.filter((datas) => {
        setFormError(validate(price));
        let prices = datas.price;
        return prices >= price.start && prices <= price.end;
      })
    );
  };
  useEffect(() => {
    gettingdata();
  }, []);

  const validate = (price) => {
    const errors = {};
    if (!price.start) {
      errors.start = "Starting value is required!";
    }
    if (!price.end) {
      errors.end = "Ending value is required!";
    }
    return errors;
  };
  return (
    <div>
      <div className="container">
        <Card>
          <h5>Filter By Price</h5>
          <div className="row">
            <div className="col-4">
              <input
                type="text"
                name="start"
                className="form-control"
                placeholder="Enter starting price"
                value={price.start}
                onChange={onChangeprice}
              ></input>
              <p style={{ color: "red" }}>{formError.start}</p>
            </div>
            <div className="col-4">
              <input
                type="text"
                name="end"
                className="form-control"
                placeholder="Enter ending price"
                value={price.end}
                onChange={onChangeprice}
              ></input>

              <p style={{ color: "red" }}>{formError.end}</p>
            </div>
            <button
              style={{ height: "40px", width: "70px" }}
              type="button"
              className="btn btn-primary col-3"
              onClick={filtereditem}
            >
              Filter
            </button>
          </div>

          <h4>Filter By Category</h4>
          <input
            style={{ width: "25%", height: "47%", margin: "2%" }}
            type="search"
            className="form-control"
            placeholder="type to search"
            onChange={(event) => {
              filteritem(event);
            }}
          ></input>
          <select className="form-select" aria-label="Default select example">
            <option defaultValue={"All"}>Open this select menu</option>
            <option value="1">One</option>
            <option value="2">Two</option>
            <option value="3">Three</option>
          </select>
          <Dropdown>
            <Dropdown.Toggle variant="success">Dropdown Button</Dropdown.Toggle>
            <Dropdown.Menu>
              {items.map((item) => (
                <Dropdown.Item onClick={() => setSelectedItem(item)}>
                  {item}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
          <pre>selectedItem: {selectedItem}</pre>
        </Card>

        <div className="row">
          {data.map((value, index) => {
            return (
              <div className="col-4" key={index}>
                <div
                  className="card "
                  style={{
                    width: "18rem",
                    height: "425px",

                    margin: "4%",
                    backgroundColor: "blanchedalmond",
                    borderBlockColor: "red",
                  }}
                >
                  <h6
                    className="card-header"
                    style={{ paddingLeft: "33px", color: "red" }}
                  >
                    {value.title}
                  </h6>

                  <img
                    className="card-img-top card-body "
                    src={value.image}
                    alt="Card image cap"
                    width="55%"
                    height="55%"
                  />

                  <h6
                    style={{
                      color: "green",
                      backgroundColor: "burlywood",
                      marginLeft: "3rem",
                      marginRight: "3rem",
                    }}
                  >
                    {`$${value.price}`}
                  </h6>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
