import React, { useEffect, useState } from "react";

import axios from "axios";
import { Card } from "react-bootstrap";

const Homepage = () => {
  const [data, getData] = useState([]);
  const [filter, getFilter] = useState([]);
  const [price, setPrice] = useState({
    start: "",
    end: "",
  });
  const [formError, setFormError] = useState({});
  const [category, setCategory] = useState([]);

  const onChangeprice = (e) => {
    setPrice({ ...price, [e.target.name]: e.target.value });
  };

  const gettingdata = () => {
    axios.get("https://fakestoreapi.com/products").then((response) => {
      getData(response.data);
      getFilter(response.data);
      setCategory(response.data);
    });
  };

  const filteritem = (event) => {
    getData(
      filter.filter((datas) => {
        console.log(filter);
        return datas.title
          .toString()
          .toUpperCase()
          .includes(event.target.value.toUpperCase());
      })
    );
  };

  const filtereditem = (e) => {
    getData(
      filter.filter((datas) => {
        setFormError(validate(price));
        let prices = datas.price;
        return prices >= price.start && prices <= price.end;
      })
    );
  };
  const categoryitem = (categories) => {
    getData(
      category.filter((datas) => {
        return datas.category
          .toString()
          .toUpperCase()
          .includes(categories.target.value.toUpperCase());
      })
    );
  };

  useEffect(() => {
    gettingdata();
  }, []);

  const validate = (price) => {
    const errors = {};
    if (!price.start) {
      errors.start = "Starting value is required!";
    }
    if (!price.end) {
      errors.end = "Ending value is required!";
    }
    return errors;
  };

  return (
    <div>
      <div className="container">
        <Card style={{ paddingLeft: "5%", marginTop: "2%" }}>
          <h6>Filter By Price</h6>
          <div className="row">
            <div className="col-md-4">
              <input
                type="text"
                name="start"
                className="form-control"
                placeholder="Enter starting price"
                value={price.start}
                onChange={onChangeprice}
              ></input>
              <p style={{ color: "red" }}>{formError.start}</p>
            </div>
            <div className="col-md-4">
              <input
                type="text"
                name="end"
                className="form-control"
                placeholder="Enter ending price"
                value={price.end}
                onChange={onChangeprice}
              ></input>

              <p style={{ color: "red" }}>{formError.end}</p>
            </div>
            <button
              style={{ height: "40px", width: "70px" }}
              type="button"
              className="btn btn-primary col-md-3"
              onClick={filtereditem}
            >
              Filter
            </button>
          </div>
          <div className="row">
            <div className="col-md-6">
              <h6>Filter By Category</h6>
              <input
                style={{ width: "25%", height: "47%", margin: "2%" }}
                type="search"
                className="form-control"
                placeholder="type to search"
                onChange={(event) => {
                  filteritem(event);
                }}
              ></input>
            </div>
            <div className="col-md-6">
              <div>
                <select
                  data-toggle="dropdown"
                  className="custom-select"
                  onChange={(categories) => {
                    categoryitem(categories);
                  }}
                >
                  <option value="men's clothing">Mens Clothing</option>
                  <option value="jewelery">Jewelery</option>
                  <option value="electronics">Electronics</option>
                  <option value="women's clothing">Womens Clothing</option>
                </select>
              </div>
            </div>
          </div>
        </Card>

        <div className="row">
          {data.map((value, index) => {
            return (
              <div className="col-md-4" key={index}>
                <div
                  className="card "
                  style={{
                    height: "25rem",

                    margin: "4%",
                    backgroundColor: "snow",
                    borderBlockColor: "black",
                  }}
                >
                  <div
                    className="card-body-center"
                    style={{
                      paddingTop: "25px ",
                      paddingLeft: "30%",
                      paddingTop: "5%",
                    }}
                  >
                    <img
                      className="card-img-center "
                      src={value.image}
                      alt="Card image cap"
                      height="215px"
                    />

                    <h6
                      className="text"
                      style={{
                        color: "blue",
                        paddingTop: "20px",
                      }}
                    >
                      {`$${value.price}`}
                    </h6>
                    <h6
                      className=" text"
                      style={{ paddingLeft: "33px", color: "blueviolet" }}
                    >
                      {value.title}
                    </h6>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
